mapper.update:
MERGE INTO TN_CM_BITCOIN_MARKET MKT USING (
SELECT #{item.marketId} AS MARKET_ID, #{item.koreaName} AS KOR_NAME, #{item.englishName} AS ENG_NAME, #{item.warning} AS WARN_CODE FROM DUAL
) T ON ( MKT.MARKET_ID = T.MARKET_ID ) WHEN MATCHED THEN UPDATE SET KOR_NAME = T.KOR_NAME, ENG_NAME = T.ENG_NAME, WARN_CODE = T.WARN_CODE, UPDATE_DATE = SYSDATE WHEN NOT MATCHED THEN INSERT ( MARKET_ID, KOR_NAME, ENG_NAME, WARN_CODE, UPDATE_DATE ) VALUES ( T.MARKET_ID, T.KOR_NAME, T.ENG_NAME, T.WARN_CODE, SYSDATE )
